@tailwind base;
@tailwind components;
@tailwind utilities;

/* Fix viewport and scrolling issues */
html {
  scroll-behavior: smooth;
}

body {
  min-height: 100vh;
  overflow-x: hidden;
}

/* Ensure main content areas have proper scrolling */
.main-content-fade {
  min-height: 100vh;
  overflow-y: auto;
  padding: 1.5rem;
}

/* Responsive sidebar adjustments */
@media (max-width: 768px) {
  .sidebar-collapsed-mobile {
    width: 0 !important;
    overflow: hidden;
  }
}

/* Custom scrollbar styling for sidebars */
.sidebar-scroll::-webkit-scrollbar {
  width: 4px;
}

.sidebar-scroll::-webkit-scrollbar-track {
  background: transparent;
}

.sidebar-scroll::-webkit-scrollbar-thumb {
  background-color: rgba(0, 0, 0, 0.05);
  border-radius: 2px;
}

.sidebar-scroll::-webkit-scrollbar-thumb:hover {
  background-color: rgba(0, 0, 0, 0.1);
}

/* Hide scrollbar for collapsed sidebar */
.sidebar-collapsed::-webkit-scrollbar {
  display: none;
}

.sidebar-collapsed {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

/* Page transition animations */
@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes fadeInUp {
  from { 
    opacity: 0; 
    transform: translateY(30px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes fadeInDown {
  from { 
    opacity: 0; 
    transform: translateY(-30px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

@keyframes slideInLeft {
  from { 
    opacity: 0; 
    transform: translateX(-30px); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

@keyframes slideInRight {
  from { 
    opacity: 0; 
    transform: translateX(30px); 
  }
  to { 
    opacity: 1; 
    transform: translateX(0); 
  }
}

@keyframes scaleIn {
  from { 
    opacity: 0; 
    transform: scale(0.95); 
  }
  to { 
    opacity: 1; 
    transform: scale(1); 
  }
}

@keyframes slideInUp {
  from { 
    opacity: 0; 
    transform: translateY(40px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

/* Enhanced page transition classes */
.page-enter {
  animation: slideInRight 0.4s cubic-bezier(0.16, 1, 0.3, 1), 
             fadeIn 0.5s cubic-bezier(0.16, 1, 0.3, 1);
}

.page-enter-up {
  animation: slideInUp 0.4s cubic-bezier(0.16, 1, 0.3, 1);
}

.page-enter-scale {
  animation: scaleIn 0.4s cubic-bezier(0.16, 1, 0.3, 1);
}

.page-enter-fade {
  animation: fadeIn 0.4s cubic-bezier(0.16, 1, 0.3, 1);
}

/* Main content fade animation - removed to fix fading issues */
.main-content-fade {
  /* Animation removed to prevent visual glitches */
}

@keyframes bellRing {
  0%, 50%, 100% { 
    transform: rotate(0deg); 
  }
  10%, 30% { 
    transform: rotate(-10deg); 
  }
  20%, 40% { 
    transform: rotate(10deg); 
  }
}

@keyframes bellBounce {
  0%, 100% { 
    transform: scale(1); 
  }
