}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer components {
  .complaint-card {
    transition: transform 0.2s ease, box-shadow 0.2s ease;
  }

  .complaint-card:hover:not([draggable="true"]:active) {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -3px rgba(0, 0, 0, 0.1);
  }

  .complaint-card[draggable="true"]:active {
    cursor: grabbing;
    transform: scale(1.05);
    z-index: 1000;
  }

  .drag-drop-zone {
    transition: all 0.3s ease;
  }

  .drag-drop-zone.drag-over {
    background-color: hsl(214, 95%, 93%);
    border-color: hsl(207, 90%, 54%);
  }

  .loading-spinner {
    border: 2px solid hsl(20, 5.9%, 90%);
    border-top: 2px solid hsl(207, 90%, 54%);
    border-radius: 50%;
    width: 20px;
    height: 20px;
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  .priority-high {
    @apply bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400;
  }

  .priority-medium {
    @apply bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400;
  }

  .priority-low {
    @apply bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-400;
  }

  .status-new {
    @apply bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-400;
  }

  .status-in-progress {
    @apply bg-yellow-100 text-yellow-800 dark:bg-yellow-900/20 dark:text-yellow-400;
  }

  .status-resolved {
    @apply bg-green-100 text-green-800 dark:bg-green-900/20 dark:text-green-400;
  }

  .status-closed {
    @apply bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-400;
  }

  /* Custom scrollbar styling for Kanban columns */
  .scrollbar-thin {
    scrollbar-width: thin;
    scrollbar-color: #d1d5db #f3f4f6;
  }

  .scrollbar-thin::-webkit-scrollbar {
    width: 6px;
  }

  .scrollbar-thin::-webkit-scrollbar-track {
    background: #f3f4f6;
    border-radius: 3px;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb {
    background-color: #d1d5db;
    border-radius: 3px;
    transition: background-color 0.2s ease;
  }

  .scrollbar-thin::-webkit-scrollbar-thumb:hover {
    background-color: #9ca3af;
  }

  /* Leaflet map z-index fixes - Keep map controls below UI elements */
  .leaflet-container {
    z-index: 1 !important;
  }

  .leaflet-control-container {
    z-index: 10 !important; /* Lower than buttons (50) to prevent overlay */
  }

  .leaflet-control-zoom {
    z-index: 10 !important;
  }

  .leaflet-popup {
    z-index: 20 !important;
  }

  .leaflet-tooltip {
    z-index: 20 !important;
  }

  /* Ensure all UI elements appear above map */
  .fixed {
    z-index: 1000 !important;
  }

  /* Dialogs and modals */
  [role="dialog"] {
    z-index: 1100 !important;
  }

  [data-radix-popper-content-wrapper] {
    z-index: 1200 !important;
  }

  /* Radix UI dialog overlays */
  [data-radix-dialog-overlay] {
    z-index: 1050 !important;
  }

  [data-radix-dialog-content] {
    z-index: 1100 !important;
  }

  /* Ensure dialog triggers don't override dialog content */
  [data-radix-dialog-trigger] {
    z-index: 50 !important;
  }

  /* Button elements inside complaint cards */
  .complaint-card button,
  .border.rounded-lg button {
    z-index: 50 !important;
    position: relative;
  }

  /* Prevent buttons from overriding open dialogs */
  body[data-scroll-locked] .complaint-card button,
  body[data-scroll-locked] .border.rounded-lg button {
    z-index: 10 !important;
  }

  /* Dropdown menus and popover content */
  [data-radix-dropdown-menu-content] {
    z-index: 1300 !important;
  }

  [data-radix-popover-content] {
    z-index: 1300 !important;
  }

  /* Tooltip content */
  [data-radix-tooltip-content] {
    z-index: 1400 !important;
  }

  /* Select content */
  [data-radix-select-content] {
    z-index: 1300 !important;
  }

  /* Navigation and notification elements */
  .relative [role="menu"] {
    z-index: 1500 !important;
  }

  /* Notification dropdown specifically */
  [data-testid="notification-dropdown"],
  .notification-dropdown {
    z-index: 1600 !important;
  }

  /* Toast notifications */
  [data-radix-toast-viewport] {
    z-index: 1700 !important;
  }

  /* Context menus */
  [role="menu"] {
    z-index: 1500 !important;
  }

  /* Any element with data-state open (active dropdowns) */
  [data-state="open"] {
    z-index: 1300 !important;
  }

  /* Specific utility classes */
  .z-50 {
    z-index: 1000 !important;
  }

  .z-40 {
    z-index: 900 !important;
  }
}

