      setCurrentUser(event.detail);
    };

    window.addEventListener('asmUserUpdated', handleUserUpdate as EventListener);

    return () => {
      window.removeEventListener('asmUserUpdated', handleUserUpdate as EventListener);
    };
  }, []);

  // Update currentUser when user prop changes
  useEffect(() => {
    setCurrentUser(user);
  }, [user]);

  const navigationItems = [
    {
      icon: Home,
      label: 'ASM Dashboard',
      href: '/asm/dashboard',
      isActive: location === '/asm/dashboard'
    },
    {
      icon: Plus,
      label: 'New Complaint',
      href: '/asm/new-complaint',
      isActive: location === '/asm/new-complaint'
    },
    {
      icon: Search,
      label: 'Track Complaints',
      href: '/asm/track-complaints',
      isActive: location === '/asm/track-complaints'
    },
    {
      icon: Settings,
      label: 'Account Settings',
      href: '/asm/settings',
      isActive: location === '/asm/settings'
    }
  ];

  return (
    <aside className={cn(
      "bg-white shadow-lg flex flex-col transition-all duration-200 ease-out sticky top-0 h-screen",
      isCollapsed ? "w-16 sidebar-collapsed overflow-hidden" : "w-64 sidebar-scroll overflow-y-auto"
    )}>
      <div className={cn(
        "border-b border-gray-200 relative transition-all duration-200 ease-out",
        isCollapsed ? "py-4 px-2 flex flex-col items-center space-y-3" : "p-6 flex items-center justify-between"
      )}>
        <div className={cn(
          "transition-all duration-200 ease-out overflow-hidden",
          isCollapsed ? "w-0 h-0 opacity-0" : "w-auto h-auto opacity-100"
        )}>
          <img 
            src={logoPath} 
            alt="BN Group Logo" 
            loading="eager"
            className="h-24 object-contain" 
          />
        </div>

        {/* Toggle button */}
        <div className={cn(
          "transition-all duration-200 ease-out flex items-center gap-2",
          isCollapsed ? "w-full flex justify-center" : ""
        )}>
          <button
            onClick={toggleSidebar}
            className={cn(
              "rounded-lg hover:bg-gray-100 transition-all duration-300 ease-in-out",
              isCollapsed ? "p-1.5" : "p-2"
            )}
          >
            <div className="transition-transform duration-300 ease-in-out">
              {isCollapsed ? (
                <Menu className="h-4 w-4 text-gray-600" />
              ) : (
                <ChevronLeft className="h-5 w-5 text-gray-600" />
              )}
            </div>
          </button>
        </div>
      </div>




      <nav className={cn(
        "flex-1 py-6 space-y-2 transition-all duration-200 ease-out",
        isCollapsed ? "px-2" : "px-4"
      )}>
        {navigationItems.map((item) => {
          const Icon = item.icon;
          return (
            <div key={item.href}>
              <div
                className={cn(
                  "flex items-center rounded-lg font-medium transition-all duration-200 ease-out cursor-pointer hover:scale-[1.02] hover:shadow-sm",
                  isCollapsed ? "px-3 py-3 justify-center" : "px-4 py-3",
                  item.isActive
                    ? "text-primary bg-blue-50 shadow-sm"
                    : "text-gray-600 hover:bg-gray-50"
                )}
                title={isCollapsed ? item.label : undefined}
                onClick={() => setLocation(item.href)}
              >
                <Icon className={cn(
                  "w-5 h-5 transition-all duration-150 ease-out",
                  !isCollapsed && "mr-3"
                )} />
                <span className={cn(
                  "transition-all duration-200 ease-out overflow-hidden whitespace-nowrap",
                  isCollapsed ? "w-0 opacity-0" : "w-auto opacity-100"
                )}>
                  {item.label}
                </span>
              </div>
            </div>
          );
        })}
      </nav>


      <div className={cn(
        "border-t border-gray-200 transition-all duration-200 ease-out",
        isCollapsed ? "p-2 space-y-2" : "p-4 space-y-3"
      )}>
        {currentUser && (
          <div className={cn(
            "flex items-center transition-all duration-200 ease-out",
            isCollapsed && "justify-center"
          )}>
            <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center text-white text-sm font-medium overflow-hidden">
              {currentUser.profilePicture ? (
                <img 
                  src={currentUser.profilePicture} 
                  alt="Profile" 
                  className="w-full h-full object-cover rounded-full"
                />
              ) : (
                <span>
                  {currentUser.firstName && currentUser.lastName 
                    ? `${currentUser.firstName.charAt(0)}${currentUser.lastName.charAt(0)}`.toUpperCase()
                    : currentUser.username ? currentUser.username.charAt(0).toUpperCase() : 'U'
                  }
                </span>
              )}
            </div>
            <div className={cn(
              "ml-3 transition-all duration-200 ease-out overflow-hidden",
              isCollapsed ? "w-0 opacity-0" : "w-auto opacity-100"
            )}>
              <p className="text-sm font-medium text-gray-900 whitespace-nowrap">{currentUser.firstName} {currentUser.lastName}</p>
              <p className="text-xs text-gray-500 whitespace-nowrap">{currentUser.email}</p>
            </div>
          </div>
        )}

        <AlertDialog>
          <AlertDialogTrigger asChild>
            <button
              className={cn(
                "flex items-center w-full rounded-lg font-medium transition-all duration-200 ease-out text-red-600 hover:bg-red-50 hover:scale-[1.02] hover:shadow-sm",
                isCollapsed ? "px-3 py-3 justify-center" : "px-4 py-3"
              )}
              title={isCollapsed ? "Logout" : undefined}
            >
              <LogOut className={cn(
                "w-5 h-5 transition-all duration-150 ease-out",
                !isCollapsed && "mr-3"
              )} />
              <span className={cn(
                "transition-all duration-200 ease-out overflow-hidden whitespace-nowrap",
                isCollapsed ? "w-0 opacity-0" : "w-auto opacity-100"
              )}>
                Logout
              </span>
            </button>
          </AlertDialogTrigger>
          <AlertDialogContent>
            <AlertDialogHeader>
              <AlertDialogTitle>Confirm Logout</AlertDialogTitle>
              <AlertDialogDescription>
                Are you sure you want to log out? You will need to sign in again to access your account.
              </AlertDialogDescription>
            </AlertDialogHeader>
            <AlertDialogFooter>
              <AlertDialogCancel>No</AlertDialogCancel>
              <AlertDialogAction
                onClick={() => {
                  logout();
                  setLocation('/');
                }}
                className="bg-red-600 hover:bg-red-700"
              >
                Yes, Logout
              </AlertDialogAction>
            </AlertDialogFooter>
          </AlertDialogContent>
        </AlertDialog>
      </div>
    </aside>
  );
}

client/src/components/charts.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { 
  PieChart, 
  Pie, 
  Cell, 
  ResponsiveContainer, 
  BarChart, 
  Bar, 
  XAxis, 
  YAxis, 
  CartesianGrid, 
  Tooltip, 
  Legend 
} from "recharts";
import { MapContainer, TileLayer, Marker, Popup, Circle } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { scaleLinear } from "d3-scale";
import type { Complaint } from "@shared/schema";

const STATUS_COLORS = {
  open: "#EF4444",
  resolved: "#10B981",
  closed: "#6B7280",
};

// Single source of truth for regional colors
const REGIONAL_COLORS = {
  'Mathura': '#DC2626',
  'Agra': '#059669',
  'Bhimasur': '#7C3AED',
  'Bhimasar': '#7C3AED'
};

// Fix for default markers in Leaflet
delete (L.Icon.Default.prototype as any)._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

interface ChartsProps {
  complaints?: Complaint[];
}

export function Charts({ complaints = [] }: ChartsProps) {
  // Calculate stats locally from the provided complaints data
  const stats = {
    total: complaints.length,
    open: complaints.filter(c => c.status === "open").length,
    resolved: complaints.filter(c => c.status === "resolved").length,
    closed: complaints.filter(c => c.status === "closed").length,
  };

  const statusData = [
    { name: "Open", value: stats.open, color: STATUS_COLORS.open },
    { name: "Resolved", value: stats.resolved, color: STATUS_COLORS.resolved },
    { name: "Closed", value: stats.closed, color: STATUS_COLORS.closed },
  ];

  // Regional data based on place of supply only (exclude empty values)
  const regionalData = complaints.reduce((acc: any[], complaint) => {
    const region = complaint.placeOfSupply;
    // Only include complaints with valid place of supply (not empty, not "-", not "Unknown")
    if (region && region !== '-' && region !== 'Unknown' && region.trim() !== '' && region !== 'Place of Supply') {
      const existing = acc.find(item => item.name === region);
      if (existing) {
        existing.value++;
      } else {
        acc.push({ name: region, value: 1 });
      }
    }
    return acc;
  }, []);

  // Area of concern data (sorted from high to low)
  const areaOfConcernData = complaints.reduce((acc: any[], complaint) => {
    const area = complaint.areaOfConcern || 'Unknown';
    const existing = acc.find(item => item.name === area);
    if (existing) {
      existing.value++;
    } else {
      acc.push({ name: area, value: 1 });
    }
    return acc;
  }, []).sort((a, b) => b.value - a.value); // Sort from high to low



  return (
    <div className="space-y-6">
      {/* Main Charts Row - Ticket Status and Regional Complaints */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Ticket Status */}
        <Card className="border border-gray-200">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-gray-900">
              Complaint Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-96">
              {statusData.length > 0 ? (
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={statusData}
                      cx="50%"
                      cy="50%"
                      outerRadius={130}
                      dataKey="value"
                      label={(entry) => {
                        // Only show label if segment is large enough (>= 5% of total)
                        const total = statusData.reduce((sum, item) => sum + item.value, 0);
                        const percentage = (entry.value / total) * 100;
                        return percentage >= 5 ? `${entry.name}: ${entry.value}` : '';
                      }}
                      labelLine={false}
                    >
                      {statusData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip formatter={(value, name) => [`${value}`, name]} />
                    <Legend />
                  </PieChart>
                </ResponsiveContainer>
              ) : (
                <div className="flex items-center justify-center h-full text-gray-500">
                  No data available
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Regional Complaints Heat Map */}
        <Card className="border border-gray-200">
          <CardHeader>
            <CardTitle className="text-lg font-semibold text-gray-900">
              Complaints by Region
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="h-96">
              {regionalData.length > 0 ? (
                <div className="w-full h-full p-2">
                  {(() => {
                    // Get max value for color scale
                    const maxValue = Math.max(...regionalData.map(item => item.value));

                    // Create color scale
                    const colorScale = scaleLinear<string>()
                      .domain([0, maxValue])
                      .range(['#E5F3FF', '#1E40AF']);

                    // State mapping for our regions
                    const stateMapping = {
                      'Mathura': 'UP',
                      'Agra': 'UP',
                      'Bhimasur': 'UP',
                      'Bhimasar': 'UP'
                    };

                    // Create heat map data for states
                    const heatMapData = regionalData.map(region => ({
                      id: stateMapping[region.name as keyof typeof stateMapping] || 'UP',
                      state: region.name,
                      value: region.value
                    }));

                    return (
                      <div className="space-y-4 relative">
                        {/* Leaflet India Map - Uttar Pradesh Focus */}
                        <div className="w-full h-64 rounded-lg border border-gray-300 overflow-hidden mb-6">
                          <div className="relative w-full h-full">
                            <MapContainer
                              center={[25.5, 74.0]} // Centered between Gujarat and UP for better visibility of all cities
                              zoom={6}
                              style={{ height: '100%', width: '100%' }}
                              scrollWheelZoom={true}
                              zoomControl={true}
                            >
                              <TileLayer
                                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                              />

                              {/* City Markers */}
                              {regionalData.map((region, index) => {
                                const positions = {
                                  'Mathura': [27.4924, 77.6736] as [number, number], // Mathura, Uttar Pradesh
                                  'Agra': [27.1767, 78.0081] as [number, number],   // Agra, Uttar Pradesh
                                  'Bhimasur': [23.20655, 69.70165] as [number, number], // Bhimasar, Kutch, Gujarat (PIN: 370160)
                                  'Bhimasar': [23.20655, 69.70165] as [number, number]  // Bhimasar, Kutch, Gujarat (PIN: 370160)
                                };

                                const position = positions[region.name as keyof typeof positions];
                                const color = REGIONAL_COLORS[region.name as keyof typeof REGIONAL_COLORS];

                                if (!position) return null;

                                // Create custom marker icon
                                const customIcon = L.divIcon({
                                  html: `
                                    <div style="
                                      background-color: ${color};
                                      border: 4px solid white;
                                      border-radius: 50%;
                                      width: ${30 + (region.value / maxValue) * 20}px;
                                      height: ${30 + (region.value / maxValue) * 20}px;
                                      box-shadow: 0 4px 8px rgba(0,0,0,0.3);
                                      display: flex;
                                      align-items: center;
                                      justify-content: center;
                                      color: white;
                                      font-weight: bold;
                                      font-size: 12px;
                                    ">
                                      ${region.value}
                                    </div>
                                  `,
                                  className: 'custom-marker',
                                  iconSize: [40, 40],
                                  iconAnchor: [20, 20]
                                });

                                return (
                                  <Marker
                                    key={index}
                                    position={position}
                                    icon={customIcon}
                                  >
                                    <Popup>
                                      <div className="text-center p-2">
                                        <h3 className="font-bold text-lg" style={{ color: color }}>
                                          {region.name}
                                        </h3>
                                        <p className="text-sm text-gray-600">
                                          <strong>{region.value}</strong> complaints
                                        </p>
                                        <p className="text-xs text-gray-500 mt-1">
                                          {region.name === 'Mathura' ? 'Uttar Pradesh - High Priority Zone' :
                                           region.name === 'Agra' ? 'Uttar Pradesh - Medium Priority Zone' :
                                           (region.name === 'Bhimasur' || region.name === 'Bhimasar') ? 'Gujarat (Kutch) - Active Monitoring Zone' :
                                           'Active Monitoring Zone'}
                                        </p>
                                      </div>
                                    </Popup>
                                  </Marker>
                                );
                              })}

                              {/* Circle overlays for better visibility */}
                              {regionalData.map((region, index) => {
                                const positions = {
                                  'Mathura': [27.4924, 77.6736] as [number, number], // Mathura, Uttar Pradesh
                                  'Agra': [27.1767, 78.0081] as [number, number],   // Agra, Uttar Pradesh
                                  'Bhimasur': [23.20655, 69.70165] as [number, number], // Bhimasar, Kutch, Gujarat (PIN: 370160)
                                  'Bhimasar': [23.20655, 69.70165] as [number, number]  // Bhimasar, Kutch, Gujarat (PIN: 370160)
                                };

                                const position = positions[region.name as keyof typeof positions];
                                const color = REGIONAL_COLORS[region.name as keyof typeof REGIONAL_COLORS];

                                if (!position) return null;

                                const radius = Math.max(8000, Math.min(25000, 8000 + (region.value / maxValue) * 15000));

                                return (
                                  <Circle
                                    key={`circle-${index}`}
                                    center={position}
                                    radius={radius}
                                    pathOptions={{
                                      color: color,
                                      fillColor: color,
                                      fillOpacity: 0.1,
                                      weight: 2,
                                      opacity: 0.6
                                    }}
                                  />
                                );
                              })}
                            </MapContainer>

                            {/* Regional Legend Overlay */}
                            <div className="absolute top-2 right-2 bg-white/95 backdrop-blur-sm rounded-lg p-3 shadow-lg border max-w-xs z-20">
                              <h4 className="text-xs font-bold text-gray-800 mb-2">Regional Distribution</h4>
                              <div className="space-y-1">
                                {regionalData.map((region, index) => {
                                  const priority = {
                                    'Mathura': 'High Priority',
                                    'Agra': 'Medium Priority',
                                    'Bhimasur': 'Active Region',
                                    'Bhimasar': 'Active Region'
                                  };

                                  return (
                                    <div key={index} className="flex items-center justify-between space-x-2">
                                      <div className="flex items-center space-x-1">
                                        <div 
                                          className="w-3 h-3 rounded-full shadow-sm"
                                          style={{ backgroundColor: REGIONAL_COLORS[region.name as keyof typeof REGIONAL_COLORS] }}
                                        />
                                        <span className="text-xs font-medium text-gray-800">{region.name}</span>
                                      </div>
                                      <div className="text-right">
                                        <div className="text-xs font-bold text-gray-900">{region.value}</div>
                                        <div className="text-xs text-gray-500">{priority[region.name as keyof typeof priority]}</div>
                                      </div>
                                    </div>
                                  );
                                })}
                              </div>

                              {/* Summary stats */}
                              <div className="mt-2 pt-2 border-t border-gray-200">
                                <div className="grid grid-cols-2 gap-2 text-xs">
                                  <div>
                                    <div className="text-sm font-bold text-blue-600">
                                      {regionalData.reduce((sum, region) => sum + region.value, 0)}
                                    </div>
                                    <div className="text-gray-600">Total</div>
                                  </div>
                                  <div>
                                    <div className="text-sm font-bold text-green-600">100%</div>
                                    <div className="text-gray-600">Coverage</div>
                                  </div>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>


                        {/* Data Summary - Positioned below map with proper spacing */}
                        <div className="grid grid-cols-3 gap-3 mt-8 relative z-10">
                          {regionalData.map((region, index) => {
                            return (
                              <div
                                key={index}
                                className="text-center p-2 rounded-lg border bg-white shadow-sm hover:shadow-md transition-shadow"
                              >
                                <div 
                                  className="w-6 h-6 rounded-sm mx-auto mb-1 flex items-center justify-center text-white font-bold text-xs"
                                  style={{ backgroundColor: REGIONAL_COLORS[region.name as keyof typeof REGIONAL_COLORS] }}
                                >
                                  {region.value}
                                </div>
                                <div className="text-xs font-medium text-gray-900">{region.name}</div>
                                <div className="text-xs text-gray-500">complaints</div>
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    );
                  })()}
                </div>
              ) : (
                <div className="flex items-center justify-center h-full text-gray-500">
                  No regional data available
                </div>
              )}
            </div>
          </CardContent>
        </Card>


      </div>
    </div>
  );
}

client/src/components/complaint-card.tsx.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { 
  Dialog, 
  DialogContent, 
  DialogHeader, 
  DialogTitle, 
  DialogTrigger 
} from "@/components/ui/dialog";
import { Building, MapPin, Package, Phone, Mail, FileText, User, Truck, Paperclip, ExternalLink, Download, MessageCircle } from "lucide-react";
import { formatRelativeTime, getPriorityColor, formatDate, hasValidAttachment } from "@/lib/utils";
import { ComplaintComments } from "./complaint-comments";
import type { Complaint } from "@shared/schema";

interface ComplaintCardProps {
  complaint: Complaint & { createdByUser?: { firstName?: string; lastName?: string; email?: string; username?: string } };
  onDragStart: () => void;
  onDragEnd: () => void;
  isDragging: boolean;
}

export function ComplaintCard({ complaint, onDragStart, onDragEnd, isDragging }: ComplaintCardProps) {
  const handleDragStart = (e: React.DragEvent) => {
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/plain', complaint.id.toString());
    onDragStart();
  };

  const handleDragEnd = (e: React.DragEvent) => {
    onDragEnd();
  };

  return (
    <Card
      className={`
        complaint-card cursor-move shadow-sm border border-gray-300 hover:shadow-md hover:border-gray-400 transition-all duration-200
        ${isDragging ? "opacity-50 scale-95 dragging" : ""}
      `}
      draggable={true}
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
    >
      <CardContent className="p-4">
        <div className="flex items-start justify-between mb-2">
          <div className="flex items-center space-x-2">
            <span className="text-xs font-medium text-gray-500">#{complaint.yearlySequenceNumber || complaint.id}</span>
            <Badge className={getPriorityColor(complaint.priority)}>
              {complaint.priority}
            </Badge>
          </div>
          <span className="text-xs text-gray-500">
            {formatRelativeTime(complaint.createdAt)}
          </span>
        </div>

        <h5 className="font-medium text-gray-900 mb-2 line-clamp-1">
          {complaint.complaintType}: {complaint.areaOfConcern && complaint.areaOfConcern !== '-' ? complaint.areaOfConcern : 'General Issue'}
        </h5>

        {/* Show who raised the complaint */}
        {complaint.createdByUser && (
          <div className="bg-amber-50 p-2 rounded mb-2">
            <div className="flex items-center text-xs text-amber-800">
              <User className="w-3 h-3 mr-1" />
              <span className="truncate">
                Raised by: {complaint.createdByUser.firstName} {complaint.createdByUser.lastName}
                {complaint.createdByUser.email && ` (${complaint.createdByUser.email})`}
              </span>
            </div>
          </div>
        )}

        {/* Always show depot name prominently if available */}
        {complaint.depoPartyName && complaint.depoPartyName !== '-' && (
          <div className="bg-blue-50 p-2 rounded mb-2">
            <div className="flex items-center text-sm font-medium text-blue-800">
              <Building className="w-4 h-4 mr-2" />
              <span className="truncate">{complaint.depoPartyName}</span>
            </div>
          </div>
        )}

        {complaint.voc && complaint.voc !== '-' && (
          <p className="text-sm text-gray-600 mb-3 line-clamp-2">
            {complaint.voc}
          </p>
        )}

        <div className="space-y-2">
          {(() => {
            // Collect available details (non-"-" fields) and show up to 3
            const availableDetails: JSX.Element[] = [];

            // Priority order of details to show
            const detailFields = [
              { field: complaint.placeOfSupply, icon: MapPin, label: 'Location' },
              { field: complaint.productName, icon: Package, label: 'Product' },
              { field: complaint.contactNumber, icon: Phone, label: 'Contact' },
              { field: complaint.email, icon: Mail, label: 'Email' },
              { field: complaint.invoiceNo, icon: FileText, label: 'Invoice' },
              { field: complaint.salePersonName, icon: User, label: 'Sales Person' },
              { field: complaint.transporterName, icon: Truck, label: 'Transporter' }
            ];

            // Filter available fields and take first 3
            detailFields.forEach(({ field, icon: Icon, label }) => {
              if (field && field !== '-' && availableDetails.length < 3) {
                availableDetails.push(
                  <div key={label} className="flex items-center text-xs text-gray-600">
                    <Icon className="w-3 h-3 mr-1 flex-shrink-0" />
                    <span className="truncate">{field}</span>
                  </div>
                );
              }
            });

            return availableDetails;
          })()}
        </div>

        <div className="mt-3 flex items-center justify-between">
          <div className="flex-1">
            {complaint.complaintSource && complaint.complaintSource !== '-' && (
              <Badge variant="outline" className="text-xs">
                {complaint.complaintSource}
              </Badge>
            )}
          </div>

          <Button 
            variant="outline" 
            size="sm"
            className="h-6 w-6 p-0"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              downloadComplaintDetails(complaint);
            }}
            onMouseDown={(e) => e.stopPropagation()}
            title="Download complaint details as text file"
          >
            <Download className="w-3 h-3" />
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

// Helper functions to get current user context
function getUserRole(): 'admin' | 'customer' {
  // Check if we're in admin context (admin token exists)
  const adminToken = localStorage.getItem('admin_token');
  if (adminToken) return 'admin';

  // Check if we're in ASM context (asm token exists)
  const asmToken = localStorage.getItem('asm_token');
  if (asmToken) return 'customer';

  // Default to admin if no tokens (shouldn't happen)
  return 'admin';
}

function getCurrentUserId(): number {
  const adminToken = localStorage.getItem('admin_token');
  const asmToken = localStorage.getItem('asm_token');

  if (adminToken) {
    try {
      const payload = JSON.parse(atob(adminToken.split('.')[1]));
      return payload.userId || 1;
    } catch {
      return 1; // Default admin ID
    }
  }

  if (asmToken) {
    try {
      const payload = JSON.parse(atob(asmToken.split('.')[1]));
      return payload.userId || 2;
    } catch {
      return 2; // Default ASM ID
    }
  }

  return 1; // Default to admin ID
}

// Function to download complaint details as text file
function downloadComplaintDetails(complaint: Complaint) {
  const formatValue = (value: any) => value && value !== '-' ? value : 'N/A';

  const content = `COMPLAINT DETAILS - #${complaint.yearlySequenceNumber || complaint.id}
=====================================

BASIC INFORMATION:
------------------
Complaint Type: ${formatValue(complaint.complaintType)}
Status: ${formatValue(complaint.status)}
Priority: ${formatValue(complaint.priority)}
Source: ${formatValue(complaint.complaintSource)}
Area of Concern: ${formatValue(complaint.areaOfConcern)}
Sub Category: ${formatValue(complaint.subCategory)}

LOCATION & PARTY DETAILS:
-------------------------
Place of Supply: ${formatValue(complaint.placeOfSupply)}
Party Name: ${formatValue(complaint.depoPartyName)}
Contact Number: ${formatValue(complaint.contactNumber)}
Email: ${formatValue(complaint.email)}

PRODUCT INFORMATION:
--------------------
Product Name: ${formatValue(complaint.productName)}
Invoice Number: ${formatValue(complaint.invoiceNo)}
Invoice Date: ${formatValue(complaint.invoiceDate)}
LR Number: ${formatValue(complaint.lrNumber)}

TRANSPORT DETAILS:
------------------
Transporter Name: ${formatValue(complaint.transporterName)}
Transporter Number: ${formatValue(complaint.transporterNumber)}

VOICE OF CUSTOMER:
------------------
${formatValue(complaint.voc)}

RESOLUTION DETAILS:
-------------------
Action Taken: ${formatValue(complaint.actionTaken)}
Person Responsible: ${formatValue(complaint.personResponsible)}
Root Cause/Action Plan: ${formatValue(complaint.rootCauseActionPlan)}

FINANCIAL DETAILS:
------------------
Credit Amount: ${formatValue(complaint.creditAmount)}
Credit Date: ${formatValue(complaint.creditDate)}
Credit Note Number: ${formatValue(complaint.creditNoteNumber)}

SALES INFORMATION:
------------------
Sales Person: ${formatValue(complaint.salePersonName)}

TIMELINE:
---------
Complaint Creation: ${formatValue(complaint.complaintCreation || complaint.createdAt)}
Date of Resolution: ${formatValue(complaint.dateOfResolution)}
Date of Closure: ${formatValue(complaint.dateOfClosure)}
Days to Resolve: ${formatValue(complaint.daysToResolve)}
Final Status: ${formatValue(complaint.finalStatus)}
Last Updated: ${formatValue(complaint.updatedAt)}

ATTACHMENT:
-----------
File: ${complaint.attachment && complaint.attachment !== '-' ? complaint.attachment.split('_').pop() : 'No attachment'}

=====================================
Generated on: ${new Date().toLocaleString()}
`;

  // Create and download the file
  const blob = new Blob([content], { type: 'text/plain' });
  const url = window.URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = `Complaint_${complaint.yearlySequenceNumber || complaint.id}_Details.txt`;
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
  window.URL.revokeObjectURL(url);
}

function ComplaintDetailDialog({ complaint }: { complaint: Complaint }) {
  return (
    <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
      <DialogHeader>
        <DialogTitle className="flex items-center justify-between">
          <span>Complaint Details - #{complaint.yearlySequenceNumber || complaint.id}</span>
          <div className="flex space-x-2">
            <Badge className={getPriorityColor(complaint.priority)}>
              {complaint.priority}
            </Badge>
          </div>
        </DialogTitle>
      </DialogHeader>

      <div className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <h4 className="font-medium mb-2">Basic Information</h4>
            <div className="space-y-1 text-sm">
              <div><strong>Type:</strong> {complaint.complaintType}</div>
              {complaint.complaintSource && complaint.complaintSource !== '-' && (
                <div><strong>Source:</strong> {complaint.complaintSource}</div>
              )}
              {complaint.areaOfConcern && complaint.areaOfConcern !== '-' && (
                <div><strong>Area of Concern:</strong> {complaint.areaOfConcern}</div>
              )}
              <div><strong>Status:</strong> {complaint.status}</div>
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2">Location & Party</h4>
            <div className="space-y-1 text-sm">
              {complaint.placeOfSupply && complaint.placeOfSupply !== '-' && (
                <div><strong>Place of Supply:</strong> {complaint.placeOfSupply}</div>
              )}
              {complaint.depoPartyName && complaint.depoPartyName !== '-' && (
                <div><strong>Party Name:</strong> {complaint.depoPartyName}</div>
              )}
              {complaint.contactNumber && complaint.contactNumber !== '-' && (
                <div><strong>Contact:</strong> {complaint.contactNumber}</div>
              )}
              {complaint.email && complaint.email !== '-' && (
                <div><strong>Email:</strong> {complaint.email}</div>
              )}
            </div>
          </div>
        </div>

        {complaint.voc && complaint.voc !== '-' && (
          <div>
            <h4 className="font-medium mb-2">Voice of Customer</h4>
            <div className="bg-gray-50 p-3 rounded-md text-sm">
              {complaint.voc}
