                    {/* Image Preview */}
                    {complaint.attachment && (complaint.attachment.endsWith('.jpg') || complaint.attachment.endsWith('.jpeg') || complaint.attachment.endsWith('.png')) && (
                      <div className="mt-2">
                        <img 
                          src={`/api/files/${encodeURIComponent(complaint.attachment)}`}
