import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { MessageCircle, Send, User, Shield } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { ComplaintComment } from "@shared/schema";

interface ComplaintCommentsProps {
  complaintId: number;
  complaintStatus: string;
  currentUserRole: 'admin' | 'customer';
  currentUserId: number;
}

export function ComplaintComments({ 
  complaintId, 
  complaintStatus, 
  currentUserRole, 
  currentUserId 
}: ComplaintCommentsProps) {
  const [newComment, setNewComment] = useState("");
  const [replyingTo, setReplyingTo] = useState<number | null>(null);
  const [replyText, setReplyText] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: comments = [], isLoading } = useQuery<ComplaintComment[]>({
    queryKey: [`/api/complaints/${complaintId}/comments`],
    refetchInterval: 1000, // Refresh every 1 second for real-time chat
  });

  const createCommentMutation = useMutation({
    mutationFn: async (data: { message: string; parentCommentId?: number }) => {
      return apiRequest(`/api/complaints/${complaintId}/comments`, {
        method: "POST",
        body: JSON.stringify({
          message: data.message,
          userRole: currentUserRole,
          userId: currentUserId,
          parentCommentId: data.parentCommentId,
        }),
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/complaints/${complaintId}/comments`] });
      setNewComment("");
      setReplyText("");
      setReplyingTo(null);
      toast({
        title: "Comment Added",
        description: "Your comment has been posted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to post comment",
        variant: "destructive",
      });
    },
  });

  const handleSubmitComment = () => {
    if (!newComment.trim()) return;
    createCommentMutation.mutate({ message: newComment });
  };

  const handleSubmitReply = (parentCommentId: number) => {
    if (!replyText.trim()) return;
    createCommentMutation.mutate({ 
      message: replyText, 
      parentCommentId 
    });
  };

  const canComment = currentUserRole === 'admin' || 
    (currentUserRole === 'customer' && complaintStatus !== 'closed');

  // Group comments by parent/child relationship
  const mainComments = comments.filter(c => !c.parentCommentId);
  const replies = comments.filter(c => c.parentCommentId);

  const getReepliesForComment = (commentId: number) => 
    replies.filter(r => r.parentCommentId === commentId);

  const formatTimeAgo = (date: string | Date) => {
    const now = new Date();
    const commentDate = new Date(date);
    const diffInMinutes = Math.floor((now.getTime() - commentDate.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return "Just now";
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return `${Math.floor(diffInMinutes / 1440)}d ago`;
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-1/4 mb-2"></div>
            <div className="h-16 bg-gray-200 rounded"></div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-4">
        {/* Chat Header */}
        <div className="flex items-center justify-between mb-4 pb-3 border-b">
          <div className="flex items-center gap-2">
            <MessageCircle className="w-5 h-5 text-blue-600" />
            <span className="font-semibold text-gray-900">
              {currentUserRole === 'admin' ? 'Respond to ASM' : 'Chat with Admin'}
            </span>
            <Badge variant="outline" className="text-xs">
              {comments.length} {comments.length === 1 ? 'message' : 'messages'}
            </Badge>
          </div>
          <div className="text-xs text-gray-500">
            Real-time updates
          </div>
        </div>

        {/* New Comment Form */}
        {canComment && (
          <div className="space-y-3 bg-gray-50 p-4 rounded-lg">
            <Textarea
              placeholder={
                currentUserRole === 'admin' 
                  ? "Type your response to the ASM..."
                  : "Ask a question or provide updates about this complaint..."
              }
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              rows={3}
              className="bg-white"
            />
            <div className="flex justify-between items-center">
              <div className="text-sm text-gray-500">
                {currentUserRole === 'customer' && complaintStatus === 'closed' && (
                  <span className="text-amber-600 font-medium">
                    Cannot chat on closed complaints
                  </span>
                )}
              </div>
              <Button
                onClick={handleSubmitComment}
                disabled={!newComment.trim() || createCommentMutation.isPending}
                size="sm"
                className="bg-blue-600 hover:bg-blue-700"
              >
                <Send className="w-4 h-4 mr-2" />
                {createCommentMutation.isPending ? 'Sending...' : 'Send Message'}
              </Button>
            </div>
          </div>
        )}

        {/* Comments List */}
        <div className="space-y-4">
          {mainComments.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <MessageCircle className="w-12 h-12 mx-auto mb-3 opacity-50" />
              <p>No comments yet</p>
              <p className="text-sm">
                {canComment 
                  ? "Be the first to add a comment" 
                  : "Comments will appear here when added"
                }
              </p>
            </div>
          ) : (
            mainComments.map((comment) => {
              const commentReplies = getReepliesForComment(comment.id);

              return (
                <div key={comment.id} className="space-y-3">
                  {/* Main Comment */}
                  <div className="border rounded-lg p-4 bg-white">
                    <div className="flex items-start justify-between mb-2">
                      <div className="flex items-center gap-2">
                        {comment.userRole === 'admin' ? (
                          <Shield className="w-4 h-4 text-blue-500" />
                        ) : (
                          <User className="w-4 h-4 text-green-500" />
                        )}
                        <Badge variant={comment.userRole === 'admin' ? 'default' : 'secondary'}>
                          {comment.userRole === 'admin' ? 'Admin' : 'ASM'}
                        </Badge>
                        <span className="text-sm text-gray-500">
                          {formatTimeAgo(comment.createdAt)}
                        </span>
                      </div>
                    </div>
                    <p className="text-gray-800 whitespace-pre-wrap">{comment.message}</p>

                    {/* Reply Button */}
                    {canComment && (
                      <div className="mt-3">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => setReplyingTo(
                            replyingTo === comment.id ? null : comment.id
                          )}
                        >
                          <MessageCircle className="w-4 h-4 mr-1" />
                          Reply
                        </Button>
                      </div>
                    )}

                    {/* Reply Form */}
                    {replyingTo === comment.id && (
                      <div className="mt-3 space-y-2">
                        <Textarea
                          placeholder="Write a reply..."
                          value={replyText}
                          onChange={(e) => setReplyText(e.target.value)}
                          rows={2}
                        />
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            onClick={() => handleSubmitReply(comment.id)}
                            disabled={!replyText.trim() || createCommentMutation.isPending}
                          >
                            <Send className="w-4 h-4 mr-1" />
                            Reply
                          </Button>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => {
                              setReplyingTo(null);
                              setReplyText("");
                            }}
                          >
                            Cancel
                          </Button>
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Replies */}
                  {commentReplies.length > 0 && (
                    <div className="ml-8 space-y-2">
                      {commentReplies.map((reply) => (
                        <div key={reply.id} className="border rounded-lg p-3 bg-gray-50">
                          <div className="flex items-center gap-2 mb-2">
                            {reply.userRole === 'admin' ? (
                              <Shield className="w-3 h-3 text-blue-500" />
                            ) : (
                              <User className="w-3 h-3 text-green-500" />
                            )}
                            <Badge 
                              variant={reply.userRole === 'admin' ? 'default' : 'secondary'}
                              className="text-xs"
                            >
                              {reply.userRole === 'admin' ? 'Admin' : 'ASM'}
                            </Badge>
                            <span className="text-xs text-gray-500">
                              {formatTimeAgo(reply.createdAt)}
                            </span>
                          </div>
                          <p className="text-sm text-gray-700 whitespace-pre-wrap">
                            {reply.message}
                          </p>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              );
            })
          )}
        </div>
    </div>
  );
}

client/src/components/complaint-form.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertComplaintSchema } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Upload, X } from "lucide-react";
import type { InsertComplaint } from "@shared/schema";

interface ComplaintFormProps {
  onSuccess: () => void;
}

export function ComplaintForm({ onSuccess }: ComplaintFormProps) {
  const [attachments, setAttachments] = useState<File[]>([]);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  const { data: options } = useQuery({
    queryKey: ["/api/complaints/options"],
  });

  const form = useForm<InsertComplaint>({
    resolver: zodResolver(insertComplaintSchema),
    defaultValues: {
      customerId: "",
      subject: "",
      description: "",
      category: "technical",
      priority: "medium",
      status: "new",
      assignedTo: "",
    },
  });

  const createComplaintMutation = useMutation({
    mutationFn: async (data: InsertComplaint) => {
      return apiRequest("POST", "/api/complaints", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/complaints"] });
      queryClient.invalidateQueries({ queryKey: ["/api/complaints/stats"] });
      toast({
        title: "Success",
        description: "Complaint submitted successfully",
      });
      form.reset();
      setAttachments([]);
      onSuccess();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit complaint",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: InsertComplaint) => {
    createComplaintMutation.mutate(data);
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setAttachments(prev => [...prev, ...files]);
  };

  const removeAttachment = (index: number) => {
    setAttachments(prev => prev.filter((_, i) => i !== index));
  };

  return (
    <>
      <DialogHeader>
        <DialogTitle>Submit New Complaint</DialogTitle>
      </DialogHeader>

      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4 mt-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <FormField
              control={form.control}
              name="customerId"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Customer ID</FormLabel>
                  <FormControl>
                    <Input placeholder="C-001" {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="priority"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Priority</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select priority" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {options?.priorities?.map((priority: string) => (
                        <SelectItem key={priority} value={priority}>
                          {priority.charAt(0).toUpperCase() + priority.slice(1)}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <FormField
            control={form.control}
            name="category"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Complaint Category</FormLabel>
                <Select onValueChange={field.onChange} defaultValue={field.value}>
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {options?.categories?.map((category: string) => (
                      <SelectItem key={category} value={category}>
                        {category.split('-').map(word => 
                          word.charAt(0).toUpperCase() + word.slice(1)
                        ).join(' ')}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="subject"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Subject</FormLabel>
                <FormControl>
                  <Input placeholder="Brief description of the issue" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="description"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Description</FormLabel>
                <FormControl>
                  <Textarea 
                    rows={4}
                    placeholder="Detailed description of the complaint..." 
                    {...field} 
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="assignedTo"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Assign To (Optional)</FormLabel>
                <FormControl>
                  <Input placeholder="Staff member name" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Attachments
            </label>
            <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
              <Upload className="w-8 h-8 text-gray-400 mx-auto mb-2" />
              <p className="text-sm text-gray-600 mb-2">
                Drag files here or <span className="text-primary">browse</span>
              </p>
              <input
                type="file"
                className="hidden"
                multiple
                accept="image/*,.pdf,.doc,.docx"
                onChange={handleFileUpload}
                id="file-upload"
              />
              <Button 
                type="button" 
                variant="outline" 
                size="sm"
                onClick={() => document.getElementById('file-upload')?.click()}
              >
                Choose Files
              </Button>
            </div>

            {attachments.length > 0 && (
              <div className="mt-2 space-y-2">
                {attachments.map((file, index) => (
                  <div key={index} className="flex items-center justify-between bg-gray-50 p-2 rounded">
                    <span className="text-sm text-gray-700">{file.name}</span>
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      onClick={() => removeAttachment(index)}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="flex items-center justify-end space-x-4 pt-4">
            <Button type="button" variant="outline" onClick={onSuccess}>
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={createComplaintMutation.isPending}
              className="bg-primary hover:bg-blue-600"
            >
              {createComplaintMutation.isPending ? "Submitting..." : "Submit Complaint"}
            </Button>
          </div>
        </form>
      </Form>
    </>
  );
}

