  return (
    <YearContext.Provider value={{ selectedYear, setSelectedYear }}>
      {children}
    </YearContext.Provider>
  );
}

export function useYear() {
  const context = useContext(YearContext);
  if (context === undefined) {
    throw new Error('useYear must be used within a YearProvider');
  }
  return context;
}

client/src/hooks/use-asm-auth.ts.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';

interface AsmUser {
  id: number;
  username: string;
  firstName: string;
  lastName: string;
  email: string;
  phone: string;
  profilePicture?: string;
}

export function useAsmAuth() {
  const [user, setUser] = useState<AsmUser | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [, setLocation] = useLocation();

  useEffect(() => {
    const checkAuth = () => {
      const asmUser = localStorage.getItem('asmUser');
      const asmToken = localStorage.getItem('asmToken');

      if (!asmUser || !asmToken) {
        setLocation('/login');
        setIsLoading(false);
        return;
      }

      try {
        const parsedUser = JSON.parse(asmUser);
        setUser(parsedUser);
      } catch (error) {
        console.error('Error parsing user data:', error);
        localStorage.removeItem('asmUser');
        localStorage.removeItem('asmToken');
        setLocation('/login');
      }

      setIsLoading(false);
    };

    checkAuth();
  }, [setLocation]);

  const logout = () => {
    localStorage.removeItem('asmToken');
    localStorage.removeItem('asmUser');
    setUser(null);
    setLocation('/login');
  };

  return {
    user,
    isLoading,
    logout,
    isAuthenticated: !!user
  };
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

